// This is the way I Did it. I found it easier to connect it to the backend.

function Login () {
    const [username, setusername] = useState('')
    const [password, setpassword] = useState('')



    const login = () => {
        Axios.post('http://localhost:5000/rest/login', {
        username: username, 
        password: password
        }).then((response)=> {
            console.log(response);
        });
    };



     return ( <div>
            <b>Log in</b>
            <br/>
            <br/>
            <form>
                <table border="0">
                    <tbody>
                        <tr>
                            <td>Username:</td>
                            <td>
                                <input type="username" name="username" size="20" autocorrect="off" spellcheck="false"
                                       autocapitalize="off" value={username} onChange={(e)=> {setusername(e.target.value)}}/>
                            </td>
                        </tr>
                        <tr>
                            <td>Password:</td>
                            <td>
                                <input type="password" name="password" size="20"
                                       required value={password} onChange={(e)=> {setpassword(e.target.value)}}/>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <br/>
                <button onClick={login}> Submit </button>
            </form>
            <a href="forgot">Forgot your password?</a>
            <br/>
            <div>
                Don't have an account? 
                <a href="/signup"> Sign Up Here!</a>
            </div>
            <br/>

        </div>
        );
}

export default Login;
