import React, { Component } from "react";
import Button from 'react-bootstrap/Button'; 
import { useState} from "react";


var ams = {book_name: "", author: "", genre: ""};
var book_title = "";
var book_author = "";
var book_genre = "";

class QuizRecs extends Component {
  constructor(props) {
    super(props)
    this.state = {
      genre1: "",
      genre2: "",
      genre3: "",
      age: "",
      page: "",
      date: "",
      triggers: "",
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }


  onSubmit = (e) => {
    e.preventDefault();
    // get form data out of state

    fetch('/takeaquiz' , {
      method: "POST",
      headers: {
        'Content-type': 'application/json'
      },
      body: JSON.stringify({
      genre1: this.state.genre1,
      genre2: this.state.genre2,
      genre3: this.state.genre3,
      age: this.state.age,
      page: this.state.page,
      date: this.state.date,
      triggers: this.state.triggers,
      })
    }).then(response => response.json())
  .then(data => ams = data)
  .then(() => console.log(ams))
  console.log(ams.book_name);
  }


    render() {
      const { classes } = this.props;
      const genre1 = this.state.genre1;
      const genre2 = this.state.genre2;
      const genre3 = this.state.genre3;
      const age = this.state.age;
      const page = this.state.page;
      const date = this.state.date;
      const triggers = this.state.triggers; 

      return (

        <div className="session">
        <h1>Quiz</h1>
        <h4>Please select three genre, age, maximum number of pages you want to read, Publication data, and Trigger warning</h4>
          <div className="register-form">
            <form onSubmit={this.onSubmit}>
              <select value={this.value} name="genre1" onChange={this.onChange}> Pick the first genre
                <option value="romance">Romance</option>
                <option value="history">History</option>
                <option value="mystery">Mystery</option>
                <option value="cooking">Cooking</option>
                <option value="fantasy">Fantasy</option>
                <option value="drama">Drama</option>
                <option value="action">Action</option>
                <option value="adventure">Adventure</option>
                <option value="detective">Detective</option>
              </select>
              <select value={this.value} name="genre2" onChange={this.onChange}> Pick the second genre genre
                <option value="romance">Romance</option>
                <option value="history">History</option>
                <option value="mystery">Mystery</option>
                <option value="cooking">Cooking</option>
                <option value="fantasy">Fantasy</option>
                <option value="drama">Drama</option>
                <option value="action">Action</option>
                <option value="adventure">Adventure</option>
                <option value="detective">Detective</option>
              </select>
              <select value={this.value} name="genre3" onChange={this.onChange}> Pick a third genre genre
                <option value="romance">Romance</option>
                <option value="history">History</option>
                <option value="mystery">Mystery</option>
                <option value="cooking">Cooking</option>
                <option value="fantasy">Fantasy</option>
                <option value="drama">Drama</option>
                <option value="action">Action</option>
                <option value="adventure">Adventure</option>
                <option value="detective">Detective</option>
              </select>
              <select value={this.value} name="age" onChange={this.onChange}> Pick a age range
                <option value="child">child</option>
                <option value="young adult">young adult</option>
                <option value="adult">adult</option>
                <option value="all">all</option>
              </select>
              <select value={this.value} name="page" onChange={this.onChange}> Pick a page range
                <option value="100">100</option>
                <option value="150">150</option>
                <option value="200">200</option>
                <option value="250">250</option>
                <option value="300">300</option>
                <option value="350">350</option>
                <option value="400">400+</option>
              </select>
              <select value={this.value} name="date" onChange={this.onChange}> Pick publication date
                <option value="1960">1960</option>
                <option value="1970">1970</option>
                <option value="1980">1980</option>
