                <option value="No trigger warning">No trigger warning</option>
              </select>
              <Button type='Submit' variant="contained" color="primary">
                Submit
              </Button>
            </form>
            <div> The answer should be here</div>
            <div>
            <h4 class="book_title">The book Title is: <span>{book_title}</span></h4>
            <h4 class="book_author">The book Author is: <span>{book_author}</span></h4>
            <h4 class="book_genre">The book Genre is: <span>{book_genre}</span></h4>
            </div>
          </div>
        </div>

      );
  }
}

