import React, { Component } from "react";
import { useEffect } from "react";
import { useState} from "react";
import {link} from "react-router-dom";


function SurpriseMeRecs() {
    useEffect( () => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch('/surpriseme');
        const items = await data.json();
        setItems(items);
    };

    return(
        <section>
        {
            items.map( item => (
                <div>
                    <h1 class="book_title2">The book Title is: </h1> <h1 class="book_title">{item.book_name}</h1>
                    <h4 class="book_genre">{item.genre}</h4>
                    <h4 class="book_rating">{item.rating}</h4>
                    <h4 class="book_author">{item.author}</h4>
                    <h4 class="book_agerange">{item.age_range}</h4>
                    <h4 class="book_pages">{item.maximum_pages}</h4>
                    <h4 class="book_publicationyear">{item.publication_date}</h4>
                    <h4 class="book_triggerwarning">{item.trigger_warning}</h4>
                </div>
            ))
        }
        </section>
    );
}

export default SurpriseMeRecs;
