import React, { Component } from "react";
import Button from 'react-bootstrap/Button'; 
const url = "/rest/submit/post";

var res = {name: "", picture: "",author: "", genre: "", synopsis: "" };
class QuizRecs extends Component {
  constructor(props) {
    super(props)
    this.state = {
      genre: '',
      age: '',
      page: '',
      date: '',
      triggers: ''
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  onSubmit = (e) => {
    e.preventDefault();
    // get form data out of state
    const { genre, age, page, date, triggers } = this.state;

    fetch('/takeaquiz' , {
      method: "POST",
      headers: {
        'Content-type': 'application/json'
      }
      .then((result) => {
        console.log(result)
        res = result
      })
      })
    }

    render() {

      const { classes } = this.props;
      const {  genre, age, page, date, triggers } = this.state;

      return (

        <div className="session">
        <h1>Quiz</h1>
          <div className="register-form">
            <form method='POST' action={url}>
              <select value={this.value} name="genre" onChange={this.handleChange}> Pick a genre
                <option value="romance">Romance</option>
                <option value="history">History</option>
                <option value="mystery">Mystery</option>
                <option value="cooking">Cooking</option>
                <option value="fantasy">Fantasy</option>
                <option value="drama">Drama</option>
                <option value="action">Action</option>
                <option value="adventure">Adventure</option>
                <option value="detective">Detective</option>
              </select>
              <select value={this.value} name="age" onChange={this.handleChange}> Pick a age range
                <option value="Child">Child</option>
                <option value="YA">YA</option>
                <option value="Adult">Adult</option>
              </select>
              <select value={this.value} name="page" onChange={this.handleChange}> Pick a page range
                <option value="100">100</option>
                <option value="200">200</option>
                <option value="400">400+</option>
              </select>
              <select value={this.value} name="date" onChange={this.handleChange}> Pick publication date
                <option value="19CE">19CE</option>
                <option value="20CE">20CE</option>
                <option value="21CE">21CE</option>
              </select>
              <select value={this.value} name="trigger" onChange={this.handleChange}> Pick a triggers
                <option value="Violence">Violence</option>
                <option value="Bullying">Bullying</option>
                <option value="Horror">Horror</option>
              </select>
              <Button type='Submit' variant="contained" color="primary">
                Submit
              </Button>
            </form>
            <div>
              {res.title}
              {res.picture}
              {res.author}
              {res.genre}
              {res.synopsis}
            </div>
          </div>
        </div>

      );
  }
}

export default QuizRecs;


