import React, { Component } from "react";
import Axios from "axios"
import { useEffect } from "react";
import { useState} from "react";
import {link} from "react-router-dom";


// const url = '/rest/signup'

class Signup extends Component {
    constructor() {
        super();
        this.state = {
            username: "",
            password: "",
            passwordConfirm: "",
            email: "",
            password: "",
            signupEmail: "",
            signupPassword: "",
            account: {},
        }
    }

    handleChange = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }

    handleSubmitSignup = (e) => {
        e.preventDefault();
        var formData = new FormData();
        formData.append('name', this.state.signupName);
        formData.append('email', this.state.signupEmail);
        formData.append('password', this.state.signupPassword);

        const requestOptions = {
            method: 'POST',
            body: formData
        };

        fetch('/rest/signup', requestOptions)
            .then(response => {
                console.log(response.status);
                console.log(response.data);
                this.setState({account: response.data});
                return response.json();
            })
            .catch(function(error) {
                console.log('Request failed', error)
            });
        alert("Succesfully Signup Up!");
    }

    render() {
     const signupName = this.state.signupName;
     const signupEmail = this.state.signupEmail;
     const signupPassword = this.state.signupPassword;
     return (  
        <div>
            <b>Create an Account</b>
            <br/>
            <b>Join our community</b>
            <br/>
            <br/>
            <form onSubmit={this.handleSubmitSignup}>
                <table border="0">
                    <tbody>
                        <tr>
                            <td>Name:</td>
                            <td>
                                <input type="username" name="username" size="20" autocorrect="off" spellcheck="false"
                                       autocapitalize="off" value={username} onChange={this.handleChange}/>
                            </td>
                        </tr>
                        <tr>
                            <td>Username:</td>
                            <td>
                                <input type="password" name="password" size="20" autocorrect="off" spellcheck="false"
                                       autocapitalize="off" value={password} onChange={this.handleChange}/>
                            </td>
                        </tr>
                        <tr>
                            <td>Password:</td>
                            <td>
                                <input type="password" name="passwordConfirm" size="20"
                                       required value={passwordConfirm} onChange={this.handleChange}/>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <br/>
                <input type="submit" value="Submit"/>
                <div>
                Already have an account? 
                    <a href="/login"> Log in Here!</a>
                </div>
            </form>
        </div>
        );
    }
}

export default Signup;

