            </div>
            <br />
            <div>
              <label>Pick which genre you would like to explore?</label>
              <br />
              <br />
              <input type="radio" name="answer1" value="Romance" onChange={this.answerSelected}/>Yes, thanks!
              <br />
              <input type="radio" name="answer1" value="Horror" onChange={this.answerSelected}/>No, sorry.
            </div>
            <input type="submit" value="submit" className="submitBtn"/>
          </form>
        </div>
    } 
        console.log(this.state.userName)
    return(
      <div>
        {userName}
        <br />
        {questions}
      </div>
    );
  }
}

export default QuizRecs;
